$(function() {
  var probe = function(service_url) {
    var result = false;
    $.ajax({
      async: false,
      type: 'HEAD',
      url: service_url,
      complete: function(request) {
        result = (request.status == 200);
      }
    });
    return result;
  }

  // debugger; // TODO: switch here, when HEAD is implemented.
  probe = function () { return false }

  var base = $('foxbase').attr('href') || $('base').attr('href') || '/';
  $('span a.print').each(function() {
    var bp = $(this).attr('href') || "/";
    if (bp[0] == '/') bp = bp.substring(1);
    if (probe(base+bp)) 
      $(this).attr('href', base + bp);
    else
      $(this).parent().hide();
  });
  $('span.service a.tip').each(function() {
    var service_tag =  $(this).attr('xeno:svc');
    var location = $(this).attr('href');
    if (location[0] == '/') location = location.substring(1);
    $(this).attr('title', $(this).text()+' service located at '+base+location);
    // Translate 'rel' to blueprint/plain
    var source = base+'blueprint/plain/'+$(this).attr('rel');
    $(this).attr('rel', source);
  }).cluetip({
    ajaxProcess: function(data) {
      var code = $('<pre class="prettyprint"/>');
      code.text(data);
      return code.get();
    },
    ajaxSettings: {   
      dataType: 'text'
    },
    onShow: function () {
      prettyPrint();
    },
    sticky: true,
    closePosition: 'title',
    closeText: '&times;', //'close', // '<img src="cross.png" alt="close" />',
    width: 800
  });
})//



















