function loadXML(service, target, result, mimetype) {
    $("body").addClass("wait-get");
    $.ajax({
        type: "GET",
        url: service.value,
        processData: false,
        complete: function (request) {
            if (2 == Math.round(request.status / 100)) {
                target.value = request.responseText;
                mimetype.value = request.getResponseHeader("Content-Type");
                result.value = request.responseText;
            } 
            else {
                var status = request.status + " " + request.statusText;
                // TODO: make an error pane
                result.value = 'HTTP/1.1 ' + status;
            }
            $("body").removeClass("wait-get");
        }
    });
}

function saveXML(service, source, result, mimetype) {
    $("body").addClass("wait-put");
    result.value = 'Saving...'
    $.ajax({
        type: "PUT",
        url: service.value,
        data: source.value,
        processData: false,
        contentType: mimetype.value,
        complete: function (request) {
            if (2 == Math.round(request.status / 100)) {
                result.value = '' + request.responseText
            } 
            else {
                var status = request.status + " " + request.statusText
                result.value = 'Error reply:\n' + status
                alert("Could not put data: " + status)
            }
            $("body").removeClass("wait-put");
        }
    });
}

function loadBluePrint(service) {
    $("#blueprint").addClass("wait-get");
    $.ajax({
        type: "GET",
        url: '/blueprint/plain' + service.value,
        processData: false,
        complete: function (request) {
            $("#blueprint").removeClass("wait-get");
            if (2 == Math.round(request.status / 100)) {
                $('#blueprint').html('<pre class="prettyprint"/>').text(request.responseText)
                prettyPrint()
            } else {
                var status = request.status + " " + request.statusText
                alert("Could not get data for" + service + ": " + status)
            }
        }
    });
}

function loadBluePrint2(service) {
  // fetch().then()...
  fetch(place.value, {
         method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, cors, *same-origin
        cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
        credentials: "same-origin", // include, *same-origin, omit
        headers: {
            "Content-Type": "application/json",
            // "Content-Type": "application/x-www-form-urlencoded",
        },
        redirect: "follow", // manual, *follow, error
        referrer: "no-referrer", // no-referrer, *client
        body: JSON.stringify(data), // body data type must match "Content-Type" header
    })

}
