$(function () {
  $("#space_creator").submit(function () {
    var form = this;
    var xml = "<xeno:space xmlns:xeno='xeno:XENOmat'>" +
      "<blueprint>" +
      "<xeno:blueprint type='text/xml' style='/blueprint/default-style'/>" + 
      "<plain><xeno:blueprint type='text/plain'/></plain>" +
      "</blueprint>" +
      "<xeno:stash type='text/html'>" +
      "Welcome to <b>" + form.name + "</b>!<br/>" +
      "You have a <a href='blueprint/'>map of your space</a> and<br/>" +
      "you can also <a href='/editor/' title='Link to a simple /editor/'>modify</a> it.<br/>" +
      "Just remember the name of this space, ok?" +
      "</xeno:stash>" +
      "</xeno:space>";
    $.ajax({
      type: form.getAttribute("method"),
      url: form.getAttribute("action"),
      data: xml,
      processData: false,
      contentType: "text/xml",
      complete: function (xhr) {
        // Do the redirection
        alert('Transfering you to "' + form.name + '" when you say "OK"!');
        var location = xhr.getResponseHeader('Location');
        document.location.href = location || form.getAttribute("action");
      }
    });
    return false;
  });
});//





